    public void CriaBanco() {
        String criaTabela = "CREATE TABLE IF NOT EXISTS " + NOME_TABELA + "(_id INTEGER PRIMARY KEY AUTOINCREMENT," + CODIGO + " TEXT, " + DESCRICAO + " TEXT" + ")";
        bancoDados = openOrCreateDatabase(NOME_BANCO, Context.MODE_PRIVATE, null);
        bancoDados.execSQL(criaTabela);
        bancoDados.getPath();
        bancoDados.close();
    }


