    public void importarLista() {
        try {
            File sdcard = Environment.getExternalStorageDirectory();
            File file = new File(sdcard, "/Download/bancoZanthus.txt");
            AbreBanco();

            BufferedReader br = new BufferedReader(new FileReader(file));
            String line;
            while ((line = br.readLine()) != null) {
                Log.i("sql", "" + line);
                String sql = line;
                bancoDados.execSQL(sql);
            }
            toast("Dados Importados com Sucesso!");
            file.delete();
        } catch (IOException e) {
            toast("Ocorreu um erro ao importar os dados: " + e);
            Log.i("aviso", "ERRO IMPORTAR:: " + e);
        }

        FechaBanco();
    }

    public void AbreBanco() {
        bancoDados = openOrCreateDatabase(NOME_BANCO, Context.MODE_PRIVATE, null);
        bancoDados.getPath();
        //Log.i("bancodadosLocal", "Caminho "; bancoDados.getPath());
    }

    public void FechaBanco() {
        bancoDados.close();
    }

    public void toast(String aviso) {
        Toast.makeText(this, aviso, Toast.LENGTH_SHORT).show();
    }

